import React from 'react';
import { FiCheckCircle, FiUsers, FiAward, FiGlobe } from 'react-icons/fi';
import '../styles/About.css';

const About = () => {
  const aboutPoints = [
    "Modern and clean design",
    "Fully responsive layout",
    "Optimized for performance",
    "SEO friendly structure",
    "Easy to customize",
    "Regular updates"
  ];

  const stats = [
    { icon: <FiUsers />, value: "10K+", label: "Users" },
    { icon: <FiAward />, value: "50+", label: "Awards" },
    { icon: <FiGlobe />, value: "100+", label: "Countries" }
  ];

  return (
    <section id="about" className="about">
      <div className="container">
        <div className="about-content">
          <div className="about-text">
            <h2 className="section-title">Why Choose <span className="text-gradient">Aurora</span></h2>
            <p className="about-description">
              Aurora is more than just a template - it's a complete solution for your next project. 
              Built with modern technologies and best practices in mind, it will save you hundreds 
              of development hours.
            </p>

            <div className="about-points">
              {aboutPoints.map((point, index) => (
                <div key={index} className="about-point">
                  <FiCheckCircle className="check-icon" />
                  <span>{point}</span>
                </div>
              ))}
            </div>

            <a href="#contact" className="btn btn-primary">
              Learn More
            </a>
          </div>

          <div className="about-stats">
            {stats.map((stat, index) => (
              <div key={index} className="stat-card">
                <div className="stat-icon">
                  {stat.icon}
                </div>
                <h3>{stat.value}</h3>
                <p>{stat.label}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
