import React from 'react';
import { motion } from 'framer-motion';
import { 
  ArrowRight, 
  Play, 
  Sparkles, 
  Zap, 
  Shield,
  TrendingUp,
  Users,
  Clock
} from 'lucide-react';
import AnimatedCard from './AnimatedCard';

const Hero = () => {
  const stats = [
    { icon: <Users className="w-6 h-6" />, value: '10K+', label: 'Happy Users', color: 'from-blue-500 to-cyan-500' },
    { icon: <TrendingUp className="w-6 h-6" />, value: '99.9%', label: 'Uptime', color: 'from-green-500 to-emerald-500' },
    { icon: <Clock className="w-6 h-6" />, value: '24/7', label: 'Support', color: 'from-purple-500 to-violet-500' },
    { icon: <Sparkles className="w-6 h-6" />, value: '50+', label: 'Features', color: 'from-pink-500 to-rose-500' },
  ];

  const features = [
    { icon: <Zap className="w-8 h-8" />, title: 'Lightning Fast', desc: 'Optimized for speed', delay: 0.2 },
    { icon: <Shield className="w-8 h-8" />, title: 'Secure', desc: 'Bank-level security', delay: 0.4 },
    { icon: <Sparkles className="w-8 h-8" />, title: 'Modern UI', desc: 'Beautiful design', delay: 0.6 },
  ];

  return (
    <section id="home" className="section-padding relative overflow-hidden">
      <div className="container-custom">
        <div className="flex flex-col lg:flex-row items-center justify-between gap-12">
          {/* Left Content */}
          <motion.div 
            className="lg:w-1/2"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
          >
            <div className="inline-flex items-center space-x-2 px-4 py-2 rounded-full bg-gradient-to-r from-primary-500/10 to-secondary-500/10 border border-primary-500/20 mb-6">
              <Sparkles className="w-4 h-4 text-primary-400" />
              <span className="text-sm font-medium text-primary-300">v2.0 Now Available</span>
            </div>

            <h1 className="text-5xl md:text-7xl font-bold font-display leading-tight mb-6">
              <span className="gradient-text">Transform</span> Your{' '}
              <span className="relative">
                Digital
                <span className="absolute -bottom-2 left-0 w-full h-1 bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full"></span>
              </span>{' '}
              Experience
            </h1>

            <p className="text-xl text-gray-400 mb-10 leading-relaxed">
              Aurora is the ultimate platform for creating stunning digital experiences. 
              With cutting-edge features and a beautiful interface, we bring your vision to life.
            </p>

            {/* Buttons */}
            <div className="flex flex-wrap gap-4 mb-12">
              <motion.button
                className="btn-primary flex items-center space-x-2"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <span>Start Free Trial</span>
                <ArrowRight className="w-5 h-5" />
              </motion.button>

              <motion.button
                className="btn-secondary flex items-center space-x-2"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <Play className="w-5 h-5" />
                <span>Watch Demo</span>
              </motion.button>
            </div>

            {/* Stats */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
              {stats.map((stat, index) => (
                <motion.div
                  key={index}
                  className="text-center"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: 0.2 + index * 0.1 }}
                >
                  <div className={`inline-flex items-center justify-center w-14 h-14 rounded-xl bg-gradient-to-br ${stat.color} mb-3`}>
                    {stat.icon}
                  </div>
                  <div className="text-2xl font-bold text-white mb-1">{stat.value}</div>
                  <div className="text-sm text-gray-400">{stat.label}</div>
                </motion.div>
              ))}
            </div>
          </motion.div>

          {/* Right Content - Animated Cards */}
          <motion.div 
            className="lg:w-1/2 relative"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            {/* Main Card */}
            <div className="relative">
              <div className="absolute -inset-1 bg-gradient-to-r from-primary-500 via-secondary-500 to-accent-500 rounded-3xl blur-xl opacity-30 animate-pulse"></div>
              <div className="relative glass-effect rounded-3xl p-8 backdrop-blur-xl">
                <div className="space-y-6">
                  {/* Dashboard Header */}
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <div className="w-3 h-3 rounded-full bg-red-500 animate-pulse"></div>
                      <div className="w-3 h-3 rounded-full bg-yellow-500"></div>
                      <div className="w-3 h-3 rounded-full bg-green-500"></div>
                    </div>
                    <div className="text-sm font-medium text-gray-400">Dashboard Preview</div>
                  </div>

                  {/* Chart Area */}
                  <div className="space-y-4">
                    <div className="h-2 w-full bg-gradient-to-r from-primary-500 to-secondary-500 rounded-full"></div>
                    <div className="h-2 w-3/4 bg-gradient-to-r from-secondary-500 to-accent-500 rounded-full"></div>
                    <div className="h-2 w-1/2 bg-gradient-to-r from-accent-500 to-primary-500 rounded-full"></div>
                  </div>

                  {/* Metrics */}
                  <div className="grid grid-cols-3 gap-4">
                    <div className="text-center p-4 rounded-xl bg-white/5">
                      <div className="text-2xl font-bold text-white">85%</div>
                      <div className="text-sm text-gray-400">Growth</div>
                    </div>
                    <div className="text-center p-4 rounded-xl bg-white/5">
                      <div className="text-2xl font-bold text-white">1.2K</div>
                      <div className="text-sm text-gray-400">Users</div>
                    </div>
                    <div className="text-center p-4 rounded-xl bg-white/5">
                      <div className="text-2xl font-bold text-white">99.9%</div>
                      <div className="text-sm text-gray-400">Uptime</div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Floating Feature Cards */}
            {features.map((feature, index) => (
              <AnimatedCard
                key={index}
                icon={feature.icon}
                title={feature.title}
                description={feature.desc}
                position={index}
                delay={feature.delay}
              />
            ))}
          </motion.div>
        </div>
      </div>

      {/* Scroll Indicator */}
      <motion.div 
        className="absolute bottom-10 left-1/2 transform -translate-x-1/2"
        animate={{ y: [0, 10, 0] }}
        transition={{ duration: 2, repeat: Infinity }}
      >
        <div className="w-6 h-10 rounded-full border-2 border-primary-500/50 flex justify-center">
