import React from 'react';
import { FiLayout, FiCode, FiSmartphone, FiDatabase, FiUsers, FiBell } from 'react-icons/fi';
import '../styles/Features.css';

const Features = () => {
  const features = [
    {
      icon: <FiLayout />,
      title: "Responsive Design",
      description: "Looks perfect on any device, from desktop to mobile",
      color: "#6c63ff"
    },
    {
      icon: <FiCode />,
      title: "Clean Code",
      description: "Well-structured and documented code for easy customization",
      color: "#36d1dc"
    },
    {
      icon: <FiSmartphone />,
      title: "Mobile First",
      description: "Designed with mobile users as the primary focus",
      color: "#ff6584"
    },
    {
      icon: <FiDatabase />,
      title: "Database Ready",
      description: "Integration with popular databases out of the box",
      color: "#4CAF50"
    },
    {
      icon: <FiUsers />,
      title: "User Management",
      description: "Complete user authentication and management system",
      color: "#FF9800"
    },
    {
      icon: <FiBell />,
      title: "Real-time Notifications",
      description: "Instant notifications for important updates and alerts",
      color: "#9C27B0"
    }
  ];

  return (
    <section id="features" className="features">
      <div className="container">
        <h2 className="section-title">Amazing <span className="text-gradient">Features</span></h2>
        <p className="section-subtitle">
          Everything you need to build modern, responsive, and feature-rich applications
        </p>

        <div className="features-grid">
          {features.map((feature, index) => (
            <div 
              key={index} 
              className="feature-card"
              style={{ '--card-color': feature.color } }
            >
              <div className="feature-icon" style={{ background: feature.color }}>
                {feature.icon}
              </div>
              <h3>{feature.title}</h3>
              <p>{feature.description}</p>
              <div className="feature-hover-effect"></div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Features;
