import React from 'react';
import { motion } from 'framer-motion';

const AnimatedCard = ({ icon, title, description, position, delay = 0 }) => {
  const positions = [
    { top: '10%', left: '-10%' },
    { top: '50%', right: '-10%' },
    { bottom: '10%', left: '-5%' },
  ];

  return (
    <motion.div
      className="absolute glass-effect rounded-2xl p-6 w-64 backdrop-blur-xl border border-white/10 card-hover"
      style={positions[position % 3]}
      initial={{ opacity: 0, scale: 0.8, y: 20 }}
      animate={{ opacity: 1, scale: 1, y: 0 }}
      transition={{
        duration: 0.6,
        delay: delay,
        type: "spring",
        stiffness: 100
      }}
      whileHover={{ 
        scale: 1.1,
        rotate: position % 2 === 0 ? 2 : -2,
        transition: { duration: 0.2 }
      }}
    >
      <div className="flex items-start space-x-4">
        <div className="flex-shrink-0">
          <div className="w-12 h-12 rounded-xl bg-gradient-to-br from-primary-500 to-secondary-500 flex items-center justify-center">
            {React.cloneElement(icon, { className: "w-6 h-6 text-white" })}
          </div>
        </div>
        <div>
          <h3 className="text-lg font-semibold text-white mb-2">{title}</h3>
          <p className="text-gray-400 text-sm">{description}</p>
        </div>
      </div>
      <div className="absolute inset-0 rounded-2xl border border-primary-500/20 pointer-events-none"></div>
    </motion.div>
  );
};

export default AnimatedCard;
