import React from 'react';
import { motion } from 'framer-motion';
import { 
  ArrowRight, 
  Play, 
  Sparkles, 
  Zap, 
  Shield,
  TrendingUp,
  Users,
  Clock,
  CheckCircle
} from 'lucide-react';
import AnimatedCard from './AnimatedCard';

const Hero = () => {
  const stats = [
    { icon: <Users className="w-5 h-5" />, value: '10K+', label: 'Active Users', color: 'from-blue-500 to-cyan-500' },
    { icon: <TrendingUp className="w-5 h-5" />, value: '99.9%', label: 'Uptime', color: 'from-emerald-500 to-green-500' },
    { icon: <Clock className="w-5 h-5" />, value: '24/7', label: 'Support', color: 'from-violet-500 to-purple-500' },
    { icon: <Sparkles className="w-5 h-5" />, value: '50+', label: 'Features', color: 'from-amber-500 to-orange-500' },
  ];

  const features = [
    { icon: <Zap className="w-6 h-6" />, title: 'Lightning Fast', desc: 'Optimized for performance', delay: 0.2 },
    { icon: <Shield className="w-6 h-6" />, title: 'Enterprise Security', desc: 'Bank-level protection', delay: 0.4 },
    { icon: <Sparkles className="w-6 h-6" />, title: 'Modern Design', desc: 'Beautiful interface', delay: 0.6 },
  ];

  const highlights = [
    'No credit card required',
    '14-day free trial',
    'Cancel anytime',
    'Priority support'
  ];

  return (
    <section id="home" className="section-padding relative overflow-hidden pt-24">
      {/* Background Grid */}
      <div className="absolute inset-0 grid-bg opacity-30"></div>

      {/* Gradient Orbs */}
      <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-primary-500/10 rounded-full blur-3xl"></div>
      <div className="absolute bottom-1/4 right-1/4 w-96 h-96 bg-accent-500/10 rounded-full blur-3xl"></div>

      <div className="container-custom relative">
        <div className="flex flex-col lg:flex-row items-center justify-between gap-12 lg:gap-20">
          {/* Left Content */}
          <motion.div 
            className="lg:w-1/2"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <div className="inline-flex items-center space-x-2 px-3 py-1.5 rounded-full bg-gradient-to-r from-primary-500/10 to-accent-500/10 border border-primary-500/20 mb-6">
              <Sparkles className="w-3.5 h-3.5 text-primary-400" />
              <span className="text-xs font-medium text-primary-300">v3.0 Now Available</span>
            </div>

            <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold font-display leading-tight mb-6 text-balance">
              Transform Your{' '}
              <span className="gradient-text">Digital</span>{' '}
              Experience with Aurora
            </h1>

            <p className="text-lg text-secondary-400 mb-8 leading-relaxed">
              Aurora is the enterprise-grade platform for creating stunning digital experiences. 
              With cutting-edge features and professional interface, we help businesses scale efficiently.
            </p>

            {/* Feature Highlights */}
            <div className="mb-8">
              {highlights.map((item, index) => (
                <div key={index} className="flex items-center space-x-3 mb-3">
                  <CheckCircle className="w-4 h-4 text-emerald-500 flex-shrink-0" />
                  <span className="text-secondary-300 text-sm">{item}</span>
                </div>
              ))}
            </div>

            {/* Buttons */}
            <div className="flex flex-wrap gap-3 mb-10">
              <motion.button
                className="btn-primary flex items-center space-x-2"
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                <span>Start Free Trial</span>
                <ArrowRight className="w-4 h-4" />
              </motion.button>

              <motion.button
                className="btn-secondary flex items-center space-x-2"
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                <Play className="w-4 h-4" />
                <span>Watch Demo</span>
              </motion.button>
            </div>

            {/* Stats */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
              {stats.map((stat, index) => (
                <motion.div
                  key={index}
                  className="text-center p-4 rounded-xl glass-effect"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: 0.2 + index * 0.1 }}
                  whileHover={{ scale: 1.05 }}
                >
                  <div className={`inline-flex items-center justify-center w-10 h-10 rounded-lg bg-gradient-to-br ${stat.color} mb-2`}>
                    {stat.icon}
                  </div>
                  <div className="text-2xl font-bold text-white mb-1">{stat.value}</div>
                  <div className="text-xs text-secondary-400 font-medium">{stat.label}</div>
                </motion.div>
              ))}
            </div>
          </motion.div>

          {/* Right Content */}
          <motion.div 
            className="lg:w-1/2 relative"
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            {/* Main Dashboard Card */}
            <div className="relative">
              <div className="absolute -inset-1 bg-gradient-to-r from-primary-500 via-accent-500 to-primary-500 rounded-2xl blur opacity-30 animate-pulse"></div>
              <div className="relative glass-effect rounded-2xl p-6 backdrop-blur-xl">
                {/* Dashboard Header */}
                <div className="flex items-center justify-between mb-6">
                  <div className="flex items-center space-x-2">
                    <div className="w-2 h-2 rounded-full bg-red-500"></div>
                    <div className="w-2 h-2 rounded-full bg-amber-500"></div>
                    <div className="w-2 h-2 rounded-full bg-emerald-500"></div>
                  </div>
                  <div className="text-sm font-medium text-secondary-400">Live Dashboard</div>
                </div>

                {/* Chart Visualization */}
                <div className="space-y-4 mb-6">
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-secondary-400">Performance</span>
                    <span className="text-sm font-medium text-emerald-500">+24%</span>
                  </div>
                  <div className="h-2 w-full bg-secondary-800 rounded-full overflow-hidden">
                    <motion.div 
                      className="h-full bg-gradient-to-r from-primary-500 to-accent-500 rounded-full"
                      initial={{ width: 0 }}
                      animate={{ width: "85%" }}
                      transition={{ duration: 1, delay: 0.5 }}
                    />
                  </div>

                  <div className="flex items-center justify-between mt-4">
                    <span className="text-sm text-secondary-400">User Growth</span>
                    <span className="text-sm font-medium text-emerald-500">+42%</span>
                  </div>
                  <div className="h-2 w-full bg-secondary-800 rounded-full overflow-hidden">
                    <motion.div 
                      className="h-full bg-gradient-to-r from-accent-500 to-primary-500 rounded-full"
                      initial={{ width: 0 }}
                      animate={{ width: "92%" }}
                      transition={{ duration: 1, delay: 0.7 }}
                    />
                  </div>
                </div>

                {/* Metrics Grid */}
                <div className="grid grid-cols-3 gap-3">
                  <div className="text-center p-3 rounded-lg bg-white/5">
                    <div className="text-lg font-bold text-white">1.2K</div>
                    <div className="text-xs text-secondary-400">Active</div>
                  </div>
                  <div className="text-center p-3 rounded-lg bg-white/5">
                    <div className="text-lg font-bold text-white">99.9%</div>
                    <div className="text-xs text-secondary-400">Uptime</div>
                  </div>
                  <div className="text-center p-3 rounded-lg bg-white/5">
                    <div className="text-lg font-bold text-white">2.4s</div>
                    <div className="text-xs text-secondary-400">Load Time</div>
                  </div>
                </div>
              </div>
            </div>

            {/* Floating Feature Cards */}
            {features.map((feature, index) => (
              <AnimatedCard
                key={index}
                icon={feature.icon}
                title={feature.title}
                description={feature.desc}
                position={index}
                delay={feature.delay}
              />
            ))}
          </motion.div>
        </div>

        {/* Trusted By Section */}
        <motion.div 
          className="mt-20 pt-8 border-t border-white/10"
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.8 }}
        >
          <p className="text-center text-sm text-secondary-400 mb-6 font-medium">TRUSTED BY INDUSTRY LEADERS</p>
          <div className="flex flex-wrap justify-center items-center gap-8 md:gap-12 opacity-70">
            {['TechCorp', 'InnovateX', 'GlobalSys', 'DataFlow', 'CloudNine'].map((company, index) => (
              <div key={index} className="text-secondary-300 font-semibold text-lg hover:text-white transition-colors">
                {company}
              </div>
            ))}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default Hero;
